    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        // TODO add your handling code here:
        Usuario c = new Usuario();
        UsuarioDAO dao = new UsuarioDAO();
        
        c.setNomecomp(txtNomeCompleto.getText());
        c.setNomeusu(txtUsuario.getText());
        c.setTelefone(txtTelefone.getText());
        c.setEmail(txtEmail.getText());
        c.setSenha(txtSenha.getText());
        
        dao.create(c);
    }//GEN-LAST:event_btnCadastrarActionPerformed

    private void btnVoltarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVoltarActionPerformed
        // TODO add your handling code here:
        int a = JOptionPane.showConfirmDialog(null, "Voc√™ deseja voltar para a tela principal?", "selecionar", JOptionPane.YES_NO_OPTION);
        if (a == 0) {
            setVisible(false);
            new ViewHome().setVisible(true);
        }
    }//GEN-LAST:event_btnVoltarActionPerformed

